create table sys_movie
(
    movie_id           bigint unsigned auto_increment comment '电影编号'
        primary key,
    movie_name         varchar(255)                         not null comment '电影名称',
    movie_length       int                                  null comment '电影时长(单位: 分钟)',
    movie_poster       varchar(1600)                        null comment '电影海报',
    movie_area         varchar(255)                         null comment '电影区域',
    release_date       datetime                             null comment '上映时间',
    movie_box_office   decimal(20, 2) unsigned default 0.00 not null comment '电影总票房',
    movie_introduction varchar(1600)                        null comment '电影简介',
    movie_pictures     varchar(1600)                        null comment '电影图集',
    del_state          tinyint                 default 0    not null comment '删除标记，0未删除，1删除，默认0',
    movie_actors       varchar(500)                         null
)
    charset = utf8;

