INSERT INTO cinema_manager.sys_movie (movie_id, movie_name, movie_length, movie_poster, movie_area, release_date, movie_box_office, movie_introduction, movie_pictures, del_state, movie_actors) VALUES (3, '你想活出怎样的人生', 124, '["/images/movie/2024/04/08/5f5e06b20e584ae5b624a007b26cb5a2.jpg"]', '日本', '2024-04-03 18:00:00', 53458.00, '电影讲述了少年牧真人的母亲葬身火海后，他随父亲与继母组成新家庭。深陷悲伤的真人阴郁孤僻，难以融入新环境。一次意外，他跟随一只会说话的苍鹭闯入废弃的神秘塔楼，却不料进入了奇幻的“亡灵世界”，开始了一场不可思议的冒险……', '["/images/movie/2024/04/08/f0bea68f5892458aa47d049cf890635c.jpg","/images/movie/2024/04/08/7778b68c8d474c49b7b64ddc1de82d46.jpg","/images/movie/2024/04/08/0427cc271dc64cbcabc89db3ccaec6fe.jpg","/images/movie/2024/04/08/190db90e20754ed9b57230756d838372.jpg","/images/movie/2024/04/08/1fd5148299ae410285f0c00dd0ad23cb.jpg"]', 0, '导演：宫崎骏
