INSERT INTO cinema_manager.sys_movie (movie_id, movie_name, movie_length, movie_poster, movie_area, release_date, movie_box_office, movie_introduction, movie_pictures, del_state, movie_actors) VALUES (7, '功夫熊猫4', 94, '["/images/movie/2024/04/08/6b0b4831337f4e8c97ec497c5f200432.jpg"]', '美国', '2024-03-22 00:00:00', 30466.00, '爷青回！《功夫熊猫》新作来袭！阿宝“升职”同时，新仇旧敌集结现身，大龙竟起死回生？狐狸小真身份神秘，到底有何心机？ 阿宝被师傅要求选出下一任神龙大侠，正苦恼如何应对时，阿宝昔日的手下败将们却纷纷重出江湖！身世神秘的狐狸小真告诉阿宝，这一切的幕后黑手正是邪恶女巫魅影妖后！阿宝能否打败魅影妖后，昔日敌人又为何再次现身？这次又会发生怎样的搞笑趣事？赶快带上亲朋好友，一起到影院寻找真相吧～', '["/images/movie/2024/04/08/33ce12f98ed34c61a9ab5a4a4854f0df.jpg","/images/movie/2024/04/08/fc9631cd547e415191a527c95bfa8dd2.jpg","/images/movie/2024/04/08/a8154851784843598841d9f4bf9bbd97.jpg","/images/movie/2024/04/08/a360afd0638a4d69ac4aef6f15b0d3c0.jpg","/images/movie/2024/04/08/14dc34b2c5074de3aed3e87fa04fae55.jpg"]', 0, '导演：迈克·米切尔
