INSERT INTO cinema_manager.sys_movie (movie_id, movie_name, movie_length, movie_poster, movie_area, release_date, movie_box_office, movie_introduction, movie_pictures, del_state, movie_actors) VALUES (9, '沙丘2', 166, '["/images/movie/2024/04/08/82e7da4234f343e297459b486c733706.jpg"]', '美国', '2024-03-08 12:00:00', 34412.00, '《沙丘2》将会探索保罗·厄崔迪的传奇之旅，他与契妮和弗雷曼人联手，踏上对致其家毁人亡的阴谋者的复仇之路。当面对一生挚爱和已知宇宙命运之间的抉择时，他必须努力阻止只有他能预见的可怕的未来。', '["/images/movie/2024/04/08/86204bc3edcc484883fe95eb5be30b6c.jpg","/images/movie/2024/04/08/a0c39364dadf46599936844b06a2b5fd.jpg","/images/movie/2024/04/08/0e5edea9f54c4b9eb2657efc6bc793d3.jpg","/images/movie/2024/04/08/88f2a6a27d304bb6b83ded1df6be729c.jpg","/images/movie/2024/04/08/605e6859cc944f2d9d2436ecdfc27989.jpg"]', 0, '导演：丹尼斯·维伦纽瓦
