INSERT INTO cinema_manager.sys_movie (movie_id, movie_name, movie_length, movie_poster, movie_area, release_date, movie_box_office, movie_introduction, movie_pictures, del_state, movie_actors) VALUES (11, '幸福慢车', 100, '["/images/movie/2024/04/08/15ee28f6e57a45f6a3012361ba2316dc.jpg"]', '中国大陆', '2024-04-09 00:00:00', 0.00, '电影《幸福慢车》是国内首部慢火车题材院线电影，展现了铁路助力乡村振兴主题，反映了铁路人平凡而生动的日常生活。影片讲述了在如今的高铁时代，以程路为代表的年轻一代列车员在慢火车上成长为合格列车员的故事，同时也表现出一代代铁路人在慢火车上奉献自己的青春，为梦想努力拼搏的热血精神。', '["/images/movie/2024/04/08/4b12e345a0314efa801eeded8a56dc34.jpg","/images/movie/2024/04/08/0cfabb8e31034603980cade01d5f3bff.jpg","/images/movie/2024/04/08/9824fb7ef31c462fb4ccfbacb27456f2.jpg","/images/movie/2024/04/08/f0ca4d3f1ded458caf289d7d734b13ac.jpg","/images/movie/2024/04/08/88b9b96547db4624a6bf6385a63fef96.jpg"]', 0, '导演：李亚东
